$(function(){
    $(".background").mouseenter(function(){
        $("#skill-per-1").animate({
            width: '70%'
        },1000);
    });
    $(".background").mouseenter(function(){
        $("#skill-per-2").animate({
            width: '60%'
        },1000);
    });
    $(".background").mouseenter(function(){
        $("#skill-per-3").animate({
            width: '20%'
        },1000);
    });
    $(".background").mouseenter(function(){
        $("#skill-per-4").animate({
            width: '10%'
        },1000);
    });
});